INSERT INTO `meta_tags` (`id`, `title_hy`, `title_en`, `title_ru`, `description_hy`, `description_en`, `description_ru`, `page`, `created_at`, `updated_at`) VALUES
(4, 'Ուսուցիչների, դասախոսների, դասատուների  և կրկնուսույցների ծառայություններ', 'Lecturers, tutors and teachers website in Armenia', 'Преподаватели, учителя, лекторы и репетиторы Армении, Ереван', 'Ուսուցիչների, դասախոսների, կրկնուսույցների, մասնավոր պարապողների և դասուցույցների առաջին մասնագիտական առցանց համայնքը Հայաստանում:', '“Dasaxos.am” is the first professional online community of teachers and tutors in Armenia.', 'Цель проекта – помощь всем желающим в получении качественных образовательных услуг.', 'home', NULL, NULL),
(5, 'նախագծի մասին / մասնավոր դասեր, դասընթացներ, դասավանդում, պարապմունքներ', 'About project / private lessons, courses, teaching, classes', 'О проекте Dasaxos.am', 'Մանուկների նախադպրոցական զարգացումից սկսած մինչև դպրոցական ընդունելության, ինչպես նաև բոլոր առարկաներով միջազգային քննությունների հանձնման նախապատրաստումը:', 'We help students to find a tutor that best meets their criteria in a convenient area and at a reasonable price.', 'От дошкольного развития малышей до подготовки к сдаче школьных, вступительных, а также международных экзаменов по всем предметам.', 'about', NULL, NULL),
(6, 'ուսուցիչ, դասախոս, դասատու, կրկնուսույց, դասուցույց / dasaxos.am', 'Tutors, lecturers, teachers / dasaxos.am', 'репетитор, преподаватель, учитель, лектор / dasaxos.am', 'Մեր դասացույցների, դասախոսների և կրկնուսույցների բազայում կան գիտության տարբեր ուղղությունների և ոլորտների մասնագետներ:', 'In the database of our tutorials, lecturers, tutors, there are specialists in different fields of science.', 'В нашей базе репетиторов есть специалисты абсолютно из разных отраслей и сфер знаний.', 'filter', NULL, NULL),
(7, 'Առարկաներ / Dasaxos.am', 'Subjects / Dasaxos.am', 'Предметы / Dasaxos.am', 'Այս բաժնի օգնությամբ դուք կգտնեք ձեզ համար անհրաժեշտ առարկայի դասախոսին։', 'With the help of this section you will find the lecturer of the subject you need.', 'С помощью данной страницы Вы найдете для Вас репетитора, преподавателя или учителя.', 'select_subject', NULL, NULL),
(8, 'Հետադարձ կապ / Dasaxos.am', 'Contact us / Dasaxos.am', 'Связаться с нами / Dasaxos.am', 'Հետադարձ կապ էջի օգնությամբ կարող եք կապ հաստատել մեզ հետ։', 'You can contact us with the help of the contact page.', 'Свяжитесь с нами заполнив форму.', 'contact', NULL, NULL),
(9, '/ Ուսուցիչ, դասախոս, դասատու, կրկնուսույց / Dasaxos.am', '/ Tutor, lecturer, teacher / Dasaxos.am', '/ репетитор, преподаватель, учитель, лектор / Dasaxos.am', NULL, NULL, NULL, 'information', NULL, NULL),
(10, 'Դասավանդողի ընտրություն / dasaxos.am', 'Select a tutor / dasaxos.am', 'Выбор репетитора / dasaxos.am', 'Մենք ձեզ  կառաջարկենք դասախոսների, որոնք բոլոր առումներով ամենահարմարն են:', 'We will definitely suggest other candidates that meet all the requirements.', 'мы обязательно предложим кандидатуры, которые соответствуют всем вашим требованиям.', 'select_teacher', NULL, NULL),
(11, 'Բլոգ / Dasaxos.am', 'Blog / Dasaxos.am', 'Блог / Dasaxos.am', 'Մեր նախագծի բլոգ բաժինը կօգնի ձեզ գտնել բազում հարցերի պատասխաններ։', 'The blog section of our project will help you find answers to many questions.', 'Наша блог страница поможет найти ответы на многие вопросы.', 'blog', NULL, NULL),
(12, 'Կարծիքներ /', 'Reviews /', 'Отзывы/', 'Կարծիքներ մեր դասախոսների, դասատուների, կրկնուսույցների և ուսուցիցչների վերաբերյալ։', 'Reviews about our lecturers, teachers, tutors և teachers.', 'Отзывы о наших лекторах, учителях, наставниках и учителях.', 'comment', NULL, NULL);
